//Groovy Script to Read Values from CSV file and assign values into the specified request for ICalculator Service
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

def csvFilePath = "C:\\Users\\kalyan.gudikandula\\Desktop\\NT\\Learn\\WebServices\\Workspace\\ICalculator.csv"
