//rowsData = context.fileReader.readLines()

rowsData = new BufferedReader(new FileReader(csvFilePath)).readLines()
def rowsize = rowsData.size()
//log.info rowsize

for(def i =1;  i < rowsize;  i++)
{

    rowdata = rowsData[i]
    String[] data = rowdata.split(",")
    //log.info data.size()
    //log.info data[1]

   //add
   def addrequestHolder = groovyUtils.getXmlHolder(context.expand('add#Request'));
    addrequestHolder.setNodeValue("//cal:x",data[0])
    addrequestHolder.setNodeValue("//cal:y",data[1])
    addrequestHolder.updateProperty()
    testRunner.runTestStepByName( "add")
    log.info 'Iteration ' +i+' for add service executed with '+data[0]+ ' and ' +data[1]+ ' values' 

   //divide
    def divrequestHolder = groovyUtils.getXmlHolder(context.expand('divide#Request'));
    divrequestHolder.setNodeValue("//cal:numerator",data[0])
    divrequestHolder.setNodeValue("//cal:denominator",data[1])
    divrequestHolder.updateProperty()
    testRunner.runTestStepByName( "divide")
    log.info 'Iteration ' +i+' for divide service executed with '+data[0]+ ' and ' +data[1]+ ' values'

    //multiply
    def mulrequestHolder = groovyUtils.getXmlHolder(context.expand('multiply#Request'));
    mulrequestHolder.setNodeValue("//cal:x",data[0])
    mulrequestHolder.setNodeValue("//cal:y",data[1])
    mulrequestHolder.updateProperty()
    testRunner.runTestStepByName( "multiply")
    log.info 'Iteration ' +i+' for multiply service executed with '+data[0]+ ' and ' +data[1]+ ' values'

    //subtract
    def subrequestHolder = groovyUtils.getXmlHolder(context.expand('subtract#Request'));
    subrequestHolder.setNodeValue("//cal:x",data[0])
    subrequestHolder.setNodeValue("//cal:y",data[1])
    subrequestHolder.updateProperty()
    testRunner.runTestStepByName( "subtract")
    log.info 'Iteration ' +i+' for subtract service executed with '+data[0]+ ' and ' +data[1]+ ' values'
}

//Read Data from CSV file and Parameterize ICalculator service operations
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
//Reading file path from Test Case Property
def csvFilePath = context.expand('${#TestCase#csvFilePath}')

def datarows = new BufferedReader(new FileReader(csvFilePath)).readLines()
def rowsize = datarows.size()
String[] headers=datarows[0].split(",")
log.info "Count of Headers in Input File:  "+headers.size()
log.info "Count Data Rows in the in Input File:  "+(rowsize-1)
for (def rowindex=1; rowindex < rowsize; rowindex++)
{
   def ScenarioData=ReadFromCSV(csvFilePath,rowindex)
   log.info "Data in row  "+ rowindex+ " is :"+ScenarioData
   //add
   def addrequestHolder = groovyUtils.getXmlHolder(context.expand('add#Request'));
    addrequestHolder.setNodeValue("//cal:x",ScenarioData['X'])
    addrequestHolder.setNodeValue("//cal:y",ScenarioData['Y'])
    addrequestHolder.updateProperty()
    testRunner.runTestStepByName( "add")
    log.info 'Iteration ' +rowindex+' for add service executed with '+ScenarioData['X']+ ' and ' +ScenarioData['Y']+ ' values'

   //divide
    def divrequestHolder = groovyUtils.getXmlHolder(context.expand('divide#Request'));
    divrequestHolder.setNodeValue("//cal:numerator",ScenarioData['X'])
    divrequestHolder.setNodeValue("//cal:denominator",ScenarioData['Y'])
    divrequestHolder.updateProperty()
    testRunner.runTestStepByName( "divide")
    log.info 'Iteration ' +rowindex+' for divide service executed with '+ScenarioData['X']+ ' and ' +ScenarioData['Y']+ ' values'

    //multiply
    def mulrequestHolder = groovyUtils.getXmlHolder(context.expand('multiply#Request'));
    mulrequestHolder.setNodeValue("//cal:x",ScenarioData['X'])
    mulrequestHolder.setNodeValue("//cal:y",ScenarioData['Y'])
    mulrequestHolder.updateProperty()
    testRunner.runTestStepByName( "multiply")
    log.info 'Iteration ' +rowindex+' for multiply service executed with '+ScenarioData['X']+ ' and ' +ScenarioData['Y']+ ' values'

    //subtract
    def subrequestHolder = groovyUtils.getXmlHolder(context.expand('subtract#Request'));
    subrequestHolder.setNodeValue("//cal:x",ScenarioData['X'])
    subrequestHolder.setNodeValue("//cal:y",ScenarioData['Y'])
    subrequestHolder.updateProperty()
    testRunner.runTestStepByName( "subtract")
    log.info 'Iteration ' +rowindex+' for subtract service executed with '+ScenarioData['X']+ ' and ' +ScenarioData['Y']+ ' values'
}

def ReadFromCSV(def FileName,def row)
{
    def map  = new HashMap<String,List>()
    def datarows = new BufferedReader(new FileReader(FileName)).readLines()
    String[] headers=datarows[0].split(",")
    String[] datarow=datarows[row].split(",")     
    for(def indx=0; indx < headers.size(); indx++)
    {
        map.put(headers[indx],datarow[indx])
    }
    return map.sort()
}
